mapper.select:
		SELECT

		FROM b_charging_info t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.gun_num = #{gunNum} 


  				AND t.charging_type = #{chargingType} 


  				AND t.outputA = #{outputa} 


  				AND t.outputB = #{outputb} 


  				AND t.outputC = #{outputc} 


  				AND t.flowA = #{flowa} 


  				AND t.flowB = #{flowb} 


  				AND t.flowC = #{flowc} 


  				AND t.dc_voltage = #{dcVoltage} 


  				AND t.d_current = #{dCurrent} 


  				AND t.charging_rate = #{chargingRate} 


  				AND t.use_power = #{usePower} 


  				AND t.soc = #{soc} 


  				AND t.vin = #{vin} 


  				AND t.ammeter_reading = #{ammeterReading} 


  				AND t.when_long = #{whenLong} 


  				AND t.surplus_when_long = #{surplusWhenLong} 


  				AND t.pile_temperature = #{pileTemperature} 


  				AND t.muzz_temperature = #{muzzTemperature} 


  				AND t.dc_temperature_num = #{dcTemperatureNum} 


  				AND t.dcTemperature = #{dctemperature} 


  				AND t.dcdy_num = #{dcdyNum} 


  				AND t.dcdy = #{dcdy} 


  				AND t.dcdy_min_num = #{dcdyMinNum} 


  				AND t.dcdy_min = #{dcdyMin} 


  				AND t.yxcdzgdy = #{yxcdzgdy} 


  				AND t.yxcddjzgdy = #{yxcddjzgdy} 


  				AND t.yxcddjzgwd = #{yxcddjzgwd} 


  				AND t.accounts_type = #{accountsType} 


  				AND t.card_num = #{cardNum} 


  				AND t.charging_accounts = #{chargingAccounts} 


  				AND t.order_num = #{orderNum} 


  				AND t.balance = #{balance} 


  				AND t.prepaid_balance = #{prepaidBalance} 


  				AND t.free_balance = #{freeBalance} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
