mapper.select:
		SELECT

		FROM b_order_charging_info t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.gun_num = #{gunNum} 


  				AND t.star_type = #{starType} 


  				AND t.end_type = #{endType} 


  				AND t.star_account = #{starAccount} 


  				AND t.account_balance = #{accountBalance} 


  				AND t.prepay_balance = #{prepayBalance} 


  				AND t.free_balance = #{freeBalance} 


  				AND t.kilowatt_now = #{kilowattNow} 


  				AND t.order_no = #{orderNo} 


  				AND t.order_type = #{orderType} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
