mapper.select:
		SELECT

		FROM log_card_result t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.gun_num = #{gunNum} 


  				AND t.result_num = #{resultNum} 


  				AND t.card_id = #{cardId} 


  				AND t.order_num = #{orderNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
