mapper.select:
		SELECT

		FROM log_vin_result t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.verification_result = #{verificationResult} 


  				AND t.gun_num = #{gunNum} 


  				AND t.order_num = #{orderNum} 


  				AND t.recharge_amount = #{rechargeAmount} 


  				AND t.deleted = #{deleted}
