mapper.select:
		SELECT

		FROM m_prescribed_rate_info t


  				AND t.id = #{id} 


  				AND t.parent_id = #{parentId} 


  				AND t.time_num = #{timeNum} 


  				AND t.star_time = #{starTime} 


  				AND t.end_time = #{endTime} 


  				AND t.power_rate = #{powerRate} 


  				AND t.service_rate = #{serviceRate} 


  				AND t.state = #{state} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5} 


				AND t.status = #{status}
