mapper.select:
		SELECT

		FROM m_remote_charging_config t


  				AND t.id = #{id} 


  				AND t.pile_type = #{pileType} 


  				AND t.pile_longitude = #{pileLongitude} 


  				AND t.pile_latitude = #{pileLatitude} 


  				AND t.carport_num = #{carportNum} 


  				AND t.parking_num = #{parkingNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.ip = #{ip} 


  				AND t.version_information = #{versionInformation} 


  				AND t.command = #{command} 


  				AND t.m_time = #{mTime} 


  				AND t.pile_num = #{pileNum} 


  				AND t.power_station_nupower_station_num = #{powerStationNupowerStationNum} 


  				AND t.area_num = #{areaNum} 


  				AND t.operation_type = #{operationType} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5} 


				AND t.status = #{status}
