mapper.select:
		SELECT

		FROM m_upgrade_command t


  				AND t.id = #{id} 


  				AND t.ftp_length = #{ftpLength} 


  				AND t.ftp = #{ftp} 


  				AND t.ftp_account = #{ftpAccount} 


  				AND t.ftp_password = #{ftpPassword} 


  				AND t.ip = #{ip} 


  				AND t.version_information = #{versionInformation} 


  				AND t.command = #{command} 


  				AND t.m_time = #{mTime} 


  				AND t.pile_num = #{pileNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5} 


				AND t.status = #{status}
