mapper.select:
		SELECT

		FROM sys_function t


  				AND t.func_id = #{funcId} 


  				AND t.parent_id = #{parentId} 


  				AND t.func_code = #{funcCode} 


  				AND t.func_name = #{funcName} 


  				AND t.func_desc = #{funcDesc} 


  				AND t.func_action = #{funcAction} 


  				AND t.stauts = #{stauts} 


  				AND t.sort_order = #{sortOrder} 


  				AND t.is_parent = #{isParent} 


  				AND t.fg_id = #{fgId} 


  				AND t.create_by = #{createBy} 


  				AND t.create_date = #{createDate} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3}
