mapper.select:
		SELECT

		FROM t_factory_manager t


  				AND t.fac_id = #{facId} 


  				AND t.name = #{name} 


  				AND t.address = #{address} 


  				AND t.phone = #{phone} 


  				AND t.linkman = #{linkman} 


  				AND t.create_by = #{createBy} 


  				AND t.create_time = #{createTime} 


  				AND t.update_by = #{updateBy} 


  				AND t.update_time = #{updateTime}
