mapper.select:
		SELECT

		FROM b_offlineInfo t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.data_num = #{dataNum} 


  				AND t.serial_num = #{serialNum} 


  				AND t.how_long = #{howLong} 


  				AND t.gun_num = #{gunNum} 


  				AND t.account_type = #{accountType} 


  				AND t.cardNum = #{cardnum} 


  				AND t.account = #{account} 


  				AND t.vin = #{vin} 


  				AND t.charging_startime = #{chargingStartime} 


  				AND t.charging_endtime = #{chargingEndtime} 


  				AND t.power_sum = #{powerSum} 


  				AND t.power_servce = #{powerServce} 


  				AND t.power_amount = #{powerAmount} 


  				AND t.charging_time_num = #{chargingTimeNum} 


  				AND t.star_soc = #{starSoc} 


  				AND t.end_soc = #{endSoc} 


  				AND t.init_kilowatt = #{initKilowatt} 


  				AND t.end_kilowatt = #{endKilowatt} 


  				AND t.order_no = #{orderNo} 


  				AND t.state = #{state} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}

SELECT
FROM b_offlineInfo t WHERE t.id IN
