mapper.select:
		SELECT

		FROM b_pile_circle_data t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.gun_num = #{gunNum} 


  				AND t.manager_state = #{managerState} 


  				AND t.charging_state = #{chargingState} 


  				AND t.net_type = #{netType} 


  				AND t.network = #{network} 


  				AND t.pile_voltage = #{pileVoltage} 


  				AND t.pile_current = #{pileCurrent} 


  				AND t.ammeter_reading = #{ammeterReading} 


  				AND t.pile_temperature = #{pileTemperature} 


  				AND t.muzz_temperature = #{muzzTemperature} 


  				AND t.inputA = #{inputa} 


  				AND t.inputB = #{inputb} 


  				AND t.inputC = #{inputc} 


  				AND t.flowA = #{flowa} 


  				AND t.flowB = #{flowb} 


  				AND t.flowC = #{flowc} 


  				AND t.charging_rate = #{chargingRate} 


  				AND t.gun_use_num = #{gunUseNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
