mapper.select:
		SELECT

		FROM log_ms_data t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.gun_num = #{gunNum} 


  				AND t.dc_type = #{dcType} 


  				AND t.dc_tcp_type = #{dcTcpType} 


  				AND t.bms_version = #{bmsVersion} 


  				AND t.dc_dealer = #{dcDealer} 


  				AND t.dcz_serial = #{dczSerial} 


  				AND t.dcz_charge_number = #{dczChargeNumber} 


  				AND t.dcz_create_time = #{dczCreateTime} 


  				AND t.dz_demand = #{dzDemand} 


  				AND t.dl_demand = #{dlDemand} 


  				AND t.dc_rated_capacity = #{dcRatedCapacity} 


  				AND t.dc_rated_voltage = #{dcRatedVoltage} 


  				AND t.rechargeable_voltage_max = #{rechargeableVoltageMax} 


  				AND t.rechargeable_current_max = #{rechargeableCurrentMax} 


  				AND t.rechargeable_temperature_max = #{rechargeableTemperatureMax} 


  				AND t.rechargeable_status = #{rechargeableStatus} 


  				AND t.dc_voltage_now = #{dcVoltageNow} 


  				AND t.voltage_out_max = #{voltageOutMax} 


  				AND t.voltage_out_min = #{voltageOutMin} 


  				AND t.current_out_max = #{currentOutMax} 


  				AND t.current_out_min = #{currentOutMin} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
