mapper.select:
		SELECT

		FROM log_query t


  				AND t.id = #{id} 


  				AND t.account_type = #{accountType} 


  				AND t.car_id = #{carId} 


  				AND t.amount = #{amount} 


  				AND t.charge_num = #{chargeNum} 


  				AND t.start_time = #{startTime} 


  				AND t.end_time = #{endTime} 


  				AND t.start_soc = #{startSoc} 


  				AND t.end_soc = #{endSoc} 


  				AND t.start_ammeter_num = #{startAmmeterNum} 


  				AND t.end_ammeter_num = #{endAmmeterNum} 


  				AND t.message = #{message} 


  				AND t.record_type = #{recordType}
