mapper.select:
		SELECT

		FROM log_tcp_message t


  				AND t.message_id = #{messageId} 


  				AND t.message_cmd = #{messageCmd} 


  				AND t.message_info = #{messageInfo} 


  				AND t.message_type = #{messageType} 


  				AND t.ip = #{ip} 


  				AND t.version_information = #{versionInformation} 


  				AND t.command = #{command} 


  				AND t.m_time = #{mTime} 


  				AND t.pile_num = #{pileNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
