mapper.select:
		SELECT

		FROM log_warning_protect_code t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.gun_num = #{gunNum} 


  				AND t.warning_code1 = #{warningCode1} 


  				AND t.warning_code2 = #{warningCode2} 


  				AND t.protect_code1 = #{protectCode1} 


  				AND t.protect_code2 = #{protectCode2} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
