mapper.select:
		SELECT

		FROM m_control_start_stop t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.cmd = #{cmd} 


  				AND t.count = #{count} 


  				AND t.balance = #{balance} 


  				AND t.pre_balance = #{preBalance} 


  				AND t.free_balance = #{freeBalance} 


  				AND t.order_num = #{orderNum} 


  				AND t.assist_power_option = #{assistPowerOption} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5} 


				AND t.status = #{status}
