mapper.select:
		SELECT

		FROM m_local_parameters t


  				AND t.id = #{id} 


  				AND t.phase_system = #{phaseSystem} 


  				AND t.max_output = #{maxOutput} 


  				AND t.pile_work_pattern = #{pileWorkPattern} 


  				AND t.off_net_charge = #{offNetCharge} 


  				AND t.off_net_charge_num = #{offNetChargeNum} 


  				AND t.usable_gun_num = #{usableGunNum} 


  				AND t.gun_use_num = #{gunUseNum} 


  				AND t.usable_pre_pile = #{usablePrePile} 


  				AND t.pile_view_loading_account = #{pileViewLoadingAccount} 


  				AND t.pile_view_loading_password = #{pileViewLoadingPassword} 


  				AND t.heart_beat_interval_time = #{heartBeatIntervalTime} 


  				AND t.server_ip = #{serverIp} 


  				AND t.server_port = #{serverPort} 


  				AND t.qrcode_pile_num = #{qrcodePileNum} 


  				AND t.gun_id = #{gunId} 


  				AND t.ip = #{ip} 


  				AND t.version_information = #{versionInformation} 


  				AND t.command = #{command} 


  				AND t.m_time = #{mTime} 


  				AND t.pile_num = #{pileNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5} 


				AND t.status = #{status}
