mapper.select:
		SELECT

		FROM m_pre_command t


  				AND t.id = #{id} 


  				AND t.pill_num = #{pillNum} 


  				AND t.pre_type = #{preType} 


  				AND t.pre_account = #{preAccount} 


  				AND t.pre_start_time = #{preStartTime} 


  				AND t.pre_end_time = #{preEndTime} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5} 


				AND t.status = #{status}
