mapper.select:
		SELECT

		FROM sys_button t


  				AND t.bt_id = #{btId} 


  				AND t.func_id = #{funcId} 


  				AND t.bt_code = #{btCode} 


  				AND t.bt_name = #{btName} 


  				AND t.create_by = #{createBy} 


  				AND t.create_date = #{createDate} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3}
