mapper.select:
		SELECT

		FROM sys_organization t


  				AND t.org_id = #{orgId} 


  				AND t.org_pinyin = #{orgPinyin} 


  				AND t.quanlujin = #{quanlujin} 


  				AND t.org_name = #{orgName} 


  				AND t.org_level = #{orgLevel} 


  				AND t.parent_id = #{parentId} 


  				AND t.status = #{status} 


  				AND t.remark = #{remark} 


  				AND t.create_by = #{createBy} 


  				AND t.create_date = #{createDate} 


  				AND t.create_ip = #{createIp} 


  				AND t.update_by = #{updateBy} 


  				AND t.update_date = #{updateDate} 


  				AND t.update_ip = #{updateIp} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4}
