mapper.select:
		SELECT

		FROM t_pile_info t


  				AND t.id = #{id} 


  				AND t.pile_num = #{pileNum} 


  				AND t.type = #{type} 


  				AND t.power = #{power} 


  				AND t.voltage = #{voltage} 


  				AND t.longitude = #{longitude} 


  				AND t.latitude = #{latitude} 


  				AND t.power_station_num = #{powerStationNum} 


  				AND t.area_num = #{areaNum} 


  				AND t.operation_type = #{operationType} 


  				AND t.gun_num_in = #{gunNumIn} 


  				AND t.muzzle_num = #{muzzleNum} 


  				AND t.create_time = #{createTime} 


  				AND t.modify_time = #{modifyTime} 


  				AND t.field1 = #{field1} 


  				AND t.field2 = #{field2} 


  				AND t.field3 = #{field3} 


  				AND t.field4 = #{field4} 


  				AND t.field5 = #{field5}
