mapper.select:
		SELECT

		FROM t_power_template_manager t


  				AND t.power_id = #{powerId} 


  				AND t.name = #{name} 


  				AND t.mark_no = #{markNo} 


  				AND t.remark = #{remark} 


  				AND t.create_by = #{createBy} 


  				AND t.create_time = #{createTime} 


  				AND t.update_by = #{updateBy} 


  				AND t.update_time = #{updateTime}
